mapper.select:
SELECT CO_DIV , CD_DIVISION , CD_CODE , CD_TITLE1 , CD_TITLE2 , CD_TITLE3 , CD_TITLE4 , CD_LENGTH FROM CD_COMMON WHERE CD_USEYN = 'Y'
AND CO_DIV = #{coDiv}
AND CD_DIVISION = #{cdDivision}
AND CD_CODE = #{cdCode}

{ CALL SP_SMS_SEND(
	#{coDiv}
	, ''
	, #{tplCd}
	, #{msPhone}
	, (SELECT CONTENT FROM SMS_TEMPLATE WHERE CO_DIV = #{coDiv} AND TPL_CODE = #{tplCd})
	, #{sendMsg}
	, ''
	, #{bkDay}
	, ''
	, #{bkCos}
	, ''
	, #{bkTime}
	, ''
	, #{toBkDay}
	, ''
	, #{toBkCos}
	, ''
	, #{toBkTime}
	, ''
	, #{bkName}
	, ''
	, #{msNum}
	, ''
	, #{msId}
	, ''
	, #{msPassword}
	, 'HOMEPAGE'
	, #{ipAddr}) }

mapper.update:
UPDATE MS_MAININFO_DETAIL
   SET MS_ID_AGREE_YN = 'Y'
 WHERE MS_NUM = #{msNum}

UPDATE MS_MAININFO_DETAIL
   SET MS_PUSH_KEY = #{pushKey}
 WHERE MS_NUM = #{msNum}

mapper.insert:

		   SELECT IFNULL(MAX(LOGIN_SEQ), 0) + 1 as loginSeq
		   FROM MS_MAININFO_LOGIN
		  WHERE MS_NUM = #{msNum}
		    AND LOGIN_DATE = DATE_FORMAT(NOW(), '%Y%m%d')

	INSERT INTO MS_MAININFO_LOGIN(
		MS_NUM
	  , LOGIN_DATE
	  , LOGIN_SEQ
	  , LOGIN_PLATFORM
	  , USER_AGENT
	  , LOGIN_AUTO
	  , INPUT_STAFF
	  , INPUT_DATETIME
	  , INPUT_IP

	) SELECT
		#{msNum}
	  , DATE_FORMAT(NOW(), '%Y%m%d')
	  , #{loginSeq}
	  , #{platform}
	  , #{userAgent}
	  , #{loginAuto}
	  , #{inputStaff}
	  , NOW()
	  , #{ipAddr}
	FROM DUAL
   WHERE 1 > (
   		 SELECT COUNT(MS_NUM) FROM MS_MAININFO_LOGIN
   		 WHERE MS_NUM = #{msNum}
		   AND LOGIN_DATE = DATE_FORMAT(NOW(), '%Y%m%d')
		   AND INPUT_DATETIME
	   BETWEEN DATE_ADD(NOW(), INTERVAL -1 MINUTE) AND NOW()
	   )
