mapper.select:
SELECT *
FROM DR_BK_HISTORY
WHERE 1=1

AND CO_DIV = #{coDiv}


AND BAY_CONDI = #{bayCondi}


AND BK_DAY = #{bkDay}


AND BK_TIME = #{bkTime}


AND MS_NUM = #{msNum}


AND BK_SERIAL_NO = #{bkSerialNo}

mapper.insert:
    	INSERT INTO DR_BK_HISTORY (
			BK_SERIAL_NO
			, CO_DIV
			, BK_DAY
			, BAY_CONDI
			, BK_TIME
			, MS_NUM
			, BK_NAME
			, BK_FIRST_PHONE
			, BK_MID_PHONE
			, BK_LAST_PHONE
			, BK_STATE
			, BK_METHOD
			, COST_DIV
			, COST_CO_DIV
			, COST_NAME
			, COST_CD
			, INPUT_STAFF
			, INPUT_DATETIME
			, INPUT_IP
    	) values (
    		#{bkSerialNo}			
    		, #{coDiv}		
    		, #{bkDay}	
    		, #{bayCondi}	
    		, #{bkTime}		
    		, #{msNum}		
    		, #{bkName}	
    		, #{bkFirstPhone}	
    		, HEX(AES_ENCRYPT(#{bkMidPhone}, 'YIDO')) 		
    		, #{bkLastPhone}		
    		, #{bkState}	
    		, 'M'			
    		, #{costDiv}	
    		, #{costCoDiv}	
    		, #{costName}	
    		, #{costCd}	
    		, 'APP'		
    		, now()
			, #{ipAddr}			
    	)
