mapper.select:
SELECT CO_DIV , BK_SERIAL_NO , CALC_SERIAL_NO , BK_DAY , BAY_CONDI , BK_TIME , MS_NUM , BK_NAME , BK_FIRST_PHONE , AES_DECRYPT(UNHEX(BK_MID_PHONE), 'YIDO') BK_MID_PHONE , BK_LAST_PHONE , BK_STATE , BK_SMS_SEND , BK_PAY_DIV , BK_METHOD , COST_CO_DIV , COST_NAME , COST_CD , COST_DIV , BK_AMOUNT , BK_DC_AMOUNT , BK_REMARK , INPUT_STAFF , INPUT_DATETIME , INPUT_IP FROM DR_BK_HISTORY WHERE 1=1
AND CO_DIV = #{coDiv}
AND BAY_CONDI = #{bayCondi}
AND BK_DAY = #{bkDay}
AND BK_TIME = #{bkTime}
AND MS_NUM = #{msNum}
AND BK_SERIAL_NO = #{bkSerialNo}
AND CALC_SERIAL_NO = #{calcSerialNo}

SELECT B.CO_NAME , B.CO_DIV , D.CD_TITLE1 AS BAY_NAME , BK_DAY , GROUP_CONCAT(CONCAT(LEFT(BK_TIME, 2),':', RIGHT(BK_TIME, 2)) ORDER BY A.BK_TIME SEPARATOR ', ') AS BK_TIME , IFNULL(NULLIF(E.MN_AMOUNT,''),'0') AS MN_AMOUNT , A.BK_STATE , F.CD_TITLE1 AS BK_STATE_NAME , I.VC_NAME , COUNT(G.BK_SERIAL_NO) AS VC_CNT , A.CALC_SERIAL_NO FROM DR_BK_HISTORY A INNER JOIN CO_PLACE B ON A.CO_DIV = B.CO_DIV INNER JOIN DR_BAY_INFO C ON A.CO_DIV = C.CO_DIV AND A.BAY_CONDI = C.BAY_CD INNER JOIN CD_COMMON D ON C.BAY_CD = D.CD_CODE AND D.CD_DIVISION = '020' AND D.CD_USEYN = 'Y' LEFT JOIN DR_BK_MN_MAP E ON A.CO_DIV = E.CO_DIV AND A.CALC_SERIAL_NO = E.BK_SERIAL_NO INNER JOIN CD_COMMON F ON A.CO_DIV = F.CO_DIV AND A.BK_STATE = F.CD_CODE AND F.CD_DIVISION = '015' AND F.CD_USEYN = 'Y' LEFT JOIN DR_VOUCHER_USE G ON A.CO_DIV = G.CO_DIV AND A.BK_SERIAL_NO = G.BK_SERIAL_NO LEFT JOIN DR_VOUCHER_SALE H ON G.VC_CO_DIV = H.CO_DIV AND G.SALE_DAY = H.SALE_DAY AND G.SALE_SEQ = H.SALE_SEQ LEFT JOIN DR_VOUCHER_CODE I ON H.CO_DIV = I.CO_DIV AND H.VC_CD = I.VC_CD AND I.USE_YN = 'Y' WHERE 1=1
AND A.CO_DIV = #{coDiv}
AND A.MS_NUM = #{msNum}
AND A.CALC_SERIAL_NO = #{calcSerialNo}
GROUP BY B.CO_NAME, D.CD_TITLE1, BK_DAY, A.CALC_SERIAL_NO ORDER BY A.CALC_SERIAL_NO DESC

SELECT CALC_SERIAL_NO FROM DR_BK_HISTORY WHERE 1=1
AND MS_NUM = #{msNum}
ORDER BY CALC_SERIAL_NO DESC LIMIT 1

mapper.insert:
    	INSERT INTO DR_BK_HISTORY (
			BK_SERIAL_NO
			, CO_DIV
			, BK_DAY
			, BAY_CONDI
			, BK_TIME
			, MS_NUM
			, BK_NAME
			, BK_FIRST_PHONE
			, BK_MID_PHONE
			, BK_LAST_PHONE
			, BK_STATE
			, BK_METHOD
			, COST_DIV
			, COST_CO_DIV
			, COST_NAME
			, COST_CD
			, INPUT_STAFF
			, INPUT_DATETIME
			, INPUT_IP
			, CALC_SERIAL_NO
			, BK_PAY_DIV
			, BK_AMOUNT
    	) values (
    		#{bkSerialNo}			
    		, #{coDiv}		
    		, #{bkDay}	
    		, #{bayCondi}	
    		, #{bkTime}		
    		, #{msNum}		
    		, #{bkName}	
    		, #{bkFirstPhone}	
    		, HEX(AES_ENCRYPT(#{bkMidPhone}, 'YIDO')) 		
    		, #{bkLastPhone}		
    		, #{bkState}	
    		, 'M'			
    		, #{costDiv}	
    		, #{costCoDiv}	
    		, #{costName}	
    		, #{costCd}	
    		, 'APP'		
    		, now()
			, #{ipAddr}		
			, #{calcSerialNo}	
			, #{bkPayDiv}		
			, #{bkAmount}			
    	)

mapper.update:
UPDATE DR_BK_HISTORY
   SET BK_STATE 	= #{bkState}
 WHERE BK_SERIAL_NO = #{bkSerialNo}

UPDATE DR_BK_HISTORY
   SET BK_AMOUNT 	= #{bkAmount}
 WHERE BK_SERIAL_NO = #{bkSerialNo}
