mapper.select:
SELECT CO_DIV , BK_DAY , BAY_CONDI , BK_TIME , BK_SEQ , BK_STATE , BK_SERIAL_NO , ENTRY_METHOD , ENTRY_USER , ENTRY_IP , ENTRY_DATETIME FROM DR_BK_MARK WHERE 1=1
AND BK_STATE = 'Y' AND (ENTRY_DATETIME IS NULL OR ENTRY_DATETIME = '')
AND CO_DIV = #{coDiv}
AND BAY_CONDI = #{bayCondi}
AND BK_DAY = #{bkDay}
AND BK_TIME = #{bkTime}
AND ENTRY_USER = #{msId}
AND BK_STATE = #{bkState}
ORDER BY BK_SEQ

SELECT CO_DIV , BK_DAY , BAY_CONDI , BK_TIME , BK_SEQ , BK_STATE , BK_SERIAL_NO , ENTRY_METHOD , ENTRY_USER , ENTRY_IP , ENTRY_DATETIME FROM DR_BK_MARK WHERE 1=1 AND BK_STATE = 'Y' AND (ENTRY_DATETIME IS NULL OR ENTRY_DATETIME = '')
AND CO_DIV = #{coDiv}
AND BAY_CONDI = #{bayCondi}
AND BK_DAY = #{bkDay}
AND BK_TIME = #{bkTime}
ORDER BY BK_SEQ

mapper.update:
    	UPDATE DR_BK_MARK
    	   SET BK_TIME = BK_TIME

			, BK_SERIAL_NO = #{bkSerialNo}


			, BK_STATE = #{bkState}


			, ENTRY_METHOD = #{entryMethod}


			, ENTRY_USER = #{updMsId}


			, ENTRY_IP = #{ipAddr}


			, ENTRY_DATETIME	= DATE_ADD(NOW(), INTERVAL #{entryTime} MINUTE)


			, ENTRY_DATETIME	= NULL

		 WHERE CO_DIV 			= #{coDiv}
		   AND BAY_CONDI 		= #{bayCondi}
		   AND BK_DAY 			= #{bkDay}
		   AND BK_TIME 			= #{bkTime}	   

		   AND BK_SEQ 			= #{bkSeq} 


		   AND ENTRY_USER 			= #{entryUser} 


		   AND BK_STATE 			= 'Y'
