mapper.select:
/*drMsMaininfoMapper.selectDuplicateUser*/ SELECT MS_NUM , MS_STATUS , MS_BK_GRANT , MS_DIVISION , MS_LEVEL , MS_LOGIN_CD , MS_LINK , MS_ID , MS_PASSWORD , MS_NAME , MS_ENAME , MS_NICKNAME , MS_IMAGENAME , MS_SEX , MS_BIRTH , MS_BIRTHYL , MS_FIRST_PHONE1 , AES_DECRYPT(UNHEX(MS_MID_PHONE1), 'JBOG') AS MS_MID_PHONE1 , MS_LAST_PHONE1 , SMS_CHK1 , MS_BLANK , MS_VOUCHER_DAY , MS_WEDDINGDAY , MS_WEDDING , MS_EMAIL , MS_EMAIL_YN , MS_HOMETEL , MS_HOMEZIP , MS_HOMEADDR1 , MS_HOMEADDR2 , MS_POST_YN , MS_COMPNM , MS_COMPNO , MS_COMPTEL , MS_COMPFAX , MS_COMPZIP , MS_COMPADDR1 , MS_COMPADDR2 , MS_COMP_POST_YN , MS_COMPDEPT , MS_COMPPOSITION , MS_REGDAY , MS_ENDDAY , MS_REMARK , MS_MKT_AGREE_YN , MS_DORMANT , LOGIN_TIME , MS_DI , INPUT_STAFF , INPUT_DATETIME , INPUT_IP , UPDATE_STAFF , UPDATE_DATETIME , UPDATE_IP FROM DR_MS_MAININFO WHERE 1 = 1
AND MS_ID = #{msId}
AND MS_FIRST_PHONE1 = #{msFirstPhone1}
AND AES_DECRYPT(UNHEX(MS_MID_PHONE1), 'JBOG') = #{msMidPhone1}
AND MS_LAST_PHONE1 = #{msLastPhone1}
AND IFNULL(NULLIF(MS_BLANK,''),'N') = 'N'

    	/*drMsMaininfoMapper.selectMsNum*/
		SELECT MS_NUM
		  FROM DR_MS_MAININFO
		 WHERE MS_ID = #{msId}
		   AND IFNULL(NULLIF(MS_BLANK,''),'N') = 'N'

mapper.insert:

    		SELECT LPAD(((IFNULL(MAX(MS_NUM), 0) ) + 1), 12, 0) AS MS_NUM FROM DR_MS_MAININFO

    	INSERT INTO DR_MS_MAININFO (
			  MS_NUM
			, MS_STATUS
			, MS_BK_GRANT
			, MS_DIVISION
			, MS_LEVEL
			, MS_LOGIN_CD
			, MS_ID
			, MS_PASSWORD
			, MS_NAME	
			, MS_FIRST_PHONE1
			, MS_MID_PHONE1
			, MS_LAST_PHONE1
			, SMS_CHK1
			, MS_BLANK
			, MS_EMAIL
			, MS_EMAIL_YN
			, MS_REGDAY		
			, MS_MKT_AGREE_YN
			, MS_DORMANT			
			, INPUT_DATETIME
			, INPUT_IP			
		) VALUES (
			  #{msNum}
			, #{msStatus}
			, #{msBkGrant}
			, #{msDivision}
			, #{msLevel}			
			, #{msLoginCd}
			, #{msId}
			, SHA2(#{msPassword}, 256)
			, #{msName}			
			, #{msFirstPhone1}
			, HEX(AES_ENCRYPT(#{msMidPhone1}, 'JBOG'))
			, #{msLastPhone1}
			, #{smsChk1}			
			, #{msBlank}			
			, #{msEmail}			
			, #{msEmailYn}			
			, DATE_FORMAT(NOW(), '%Y%m%d')		
			, #{msMktAgreeYn}
			, #{msDormant}
			, NOW()
			, #{ipAddr}						
		)

mapper.select:
SELECT COUNT(*)
	FROM DR_MS_MAININFO M INNER JOIN CO_PLACE P ON P.CO_DIV = #{coDiv}
WHERE M.MS_ID = #{msId}
AND IFNULL(NULLIF(MS_BLANK,''),'N') = 'N'
