mapper.select:
SELECT MS_NUM , MS_STATUS , MS_BK_GRANT , MS_DIVISION , MS_LEVEL , MS_LOGIN_CD , MS_LINK , MS_ID , MS_PASSWORD , MS_NAME , MS_ENAME , MS_NICKNAME , MS_IMAGENAME , MS_SEX , MS_BIRTH , MS_BIRTHYL , MS_FIRST_PHONE1 , AES_DECRYPT(UNHEX(MS_MID_PHONE1), 'YIDO') AS MS_MID_PHONE1 , MS_LAST_PHONE1 , SMS_CHK1 , MS_BLANK , MS_VOUCHER_DAY , MS_WEDDINGDAY , MS_WEDDING , AES_DECRYPT(UNHEX(MS_EMAIL), 'YIDO') AS MS_EMAIL , MS_EMAIL_YN , AES_DECRYPT(UNHEX(MS_HOMETEL), 'YIDO') AS MS_HOMETEL , MS_HOMEZIP , MS_HOMEADDR1 , MS_HOMEADDR2 , MS_POST_YN , MS_COMPNM , MS_COMPNO , AES_DECRYPT(UNHEX(MS_COMPTEL), 'YIDO') AS MS_COMPTEL , AES_DECRYPT(UNHEX(MS_COMPFAX), 'YIDO') AS MS_COMPFAX , MS_COMPZIP , MS_COMPADDR1 , MS_COMPADDR2 , MS_COMP_POST_YN , MS_COMPDEPT , MS_COMPPOSITION , MS_REGDAY , MS_ENDDAY , MS_REMARK , MS_MKT_AGREE_YN , MS_DORMANT , LOGIN_TIME , MS_DI , INPUT_STAFF , INPUT_DATETIME , INPUT_IP , UPDATE_STAFF , UPDATE_DATETIME , UPDATE_IP FROM DR_MS_MAININFO WHERE 1=1
AND MS_ID = #{userId}
AND MS_NUM = #{msNum}
AND IFNULL(NULLIF(MS_BLANK,''),'N') = 'N'

    	/* drMsMaininfoMapper.selectMsNum - MS_ID로 MS_NUM 조회 */
		SELECT MS_NUM
		  FROM DR_MS_MAININFO
		 WHERE MS_ID = #{msId}
		   AND MS_LOGIN_CD = #{msLoginCd}
		   AND IFNULL(NULLIF(MS_BLANK,''),'N') = 'N'

mapper.insert:

    		SELECT LPAD(((IFNULL(MAX(MS_NUM), 0) ) + 1), 12, 0) AS MS_NUM FROM DR_MS_MAININFO

    	INSERT INTO DR_MS_MAININFO (
			  MS_NUM
			, MS_STATUS
			, MS_BK_GRANT
			, MS_DIVISION
			, MS_LEVEL
			, MS_LOGIN_CD
			, MS_ID
			, MS_PASSWORD
			, MS_NAME	
			, MS_FIRST_PHONE1
			, MS_MID_PHONE1
			, MS_LAST_PHONE1
			, SMS_CHK1
			, MS_BLANK
			, MS_EMAIL
			, MS_EMAIL_YN
			, MS_REGDAY		
			, MS_MKT_AGREE_YN
			, MS_DORMANT
			, INPUT_STAFF			
			, INPUT_DATETIME
			, INPUT_IP	
		) VALUES (
			  #{msNum}
			, #{msStatus}
			, #{msBkGrant}
			, #{msDivision}
			, #{msLevel}			
			, #{msLoginCd}
			, #{msId}
			, SHA2(#{msPassword}, 256)
			, #{msName}			
			, #{msFirstPhone1}
			, HEX(AES_ENCRYPT(#{msMidPhone1}, 'YIDO'))
			, #{msLastPhone1}
			, #{smsChk1}			
			, #{msBlank}			
			, HEX(AES_ENCRYPT(#{msEmail}, 'YIDO'))		
			, #{msEmailYn}			
			, DATE_FORMAT(NOW(), '%Y%m%d')		
			, #{msMktAgreeYn}
			, #{msDormant}
			, #{inputStaff}
			, NOW()
			, #{ipAddr}						
		)

mapper.update:
    	UPDATE DR_MS_MAININFO
    		SET MS_NUM = #{msNum}

			, MS_PASSWORD = SHA2(#{newMsPassword}, 256)


			, MS_NAME = #{msName}


			, SMS_CHK1 = #{smsChk1}


			, MS_FIRST_PHONE1 = #{msFirstPhone1}
			, MS_MID_PHONE1 = #{msMidPhone1}
			, MS_LAST_PHONE1 = #{msLastPhone1}

			, UPDATE_STAFF = #{updateStaff}
			, UPDATE_DATETIME = NOW()
			, UPDATE_IP = #{updateIp}
		 WHERE MS_NUM = #{msNum}

mapper.select:
SELECT MS_NUM , MS_ID , MS_LOGIN_CD FROM DR_MS_MAININFO M WHERE 1 = 1
AND MS_ID = #{msId}
AND MS_NAME = #{msName}
AND CONCAT(MS_FIRST_PHONE1, '-', AES_DECRYPT(UNHEX(MS_MID_PHONE1), 'YIDO'), '-', MS_LAST_PHONE1 ) = #{msPhone}
AND NVL(MS_BLANK, 'N') = 'N' LIMIT 1

mapper.update:
UPDATE DR_MS_MAININFO
   SET MS_PASSWORD = SHA2(#{newMsPassword}, 256)
 WHERE MS_NUM = #{msNum}

UPDATE DR_MS_MAININFO

	  SET MS_SESSION_KEY = #{msSessionKey}
	, MS_SESSION_LIMIT = NOW() + INTERVAL 30 DAY


	  SET MS_SESSION_KEY = NULL
	, MS_SESSION_LIMIT = NULL

WHERE MS_NUM = #{msNum}

mapper.insert:

	SELECT IFNULL(MAX(LOGIN_SEQ), 0) + 1 as loginSeq
	FROM MS_MAININFO_LOGIN
	WHERE 1 = 1
		AND MS_NUM = #{msNum}
		AND LOGIN_DATE = DATE_FORMAT(NOW(), '%Y%m%d')

INSERT INTO MS_MAININFO_LOGIN (
	MS_NUM
  , LOGIN_DATE
  , LOGIN_SEQ
  , LOGIN_PLATFORM
  , USER_AGENT
  , LOGIN_AUTO
  , INPUT_STAFF
  , INPUT_DATETIME
  , INPUT_IP
) SELECT
	#{msNum}
  , DATE_FORMAT(NOW(), '%Y%m%d')
  , #{loginSeq}
  , #{platform}
  , #{userAgent}
  , #{loginAuto}
  , #{inputStaff}
  , NOW()
  , #{ipAddr}
FROM DUAL
WHERE 1 > (
	SELECT COUNT(MS_NUM) 
	FROM MS_MAININFO_LOGIN
	WHERE 1 = 1
		AND MS_NUM = #{msNum}
		AND LOGIN_DATE = DATE_FORMAT(NOW(), '%Y%m%d')
		AND INPUT_DATETIME
	BETWEEN DATE_ADD(NOW(), INTERVAL -1 MINUTE) AND NOW()
   )
