mapper.select:
SELECT A.CO_DIV , SALE_SEQ , A.SALE_DAY , VC_NAME , VC_MONTH , A.VC_LIMIT_CNT , A.VC_REM_CNT , DATE_FORMAT(A.VC_TO_DAY, "%Y.%m.%d") AS VC_TO_DAY , FLOOR(A.VC_AMOUNT / A.VC_LIMIT_CNT) AS AMOUNT FROM DR_VOUCHER_SALE A INNER JOIN DR_VOUCHER_CODE B ON A.VC_CD = B.VC_CD INNER JOIN DR_BK_TIME_VOUCHER C ON B.CO_DIV = C.CO_DIV AND B.VC_CD = C.VC_CD WHERE 1=1
AND C.CO_DIV = #{coDiv}
AND C.BAY_CONDI = #{bayCondi}
AND C.BK_DAY = #{bkDay}
AND C.BK_TIME = #{bkTime}
AND A.MS_NUM = #{msNum}
AND A.USE_YN = 'Y'

    SELECT *
	  FROM DR_VOUCHER_SALE 
	 WHERE 1=1 

	  AND CO_DIV = #{coDiv}


	  AND SALE_DAY = #{saleDay}


	  AND SALE_SEQ = #{saleSeq}

mapper.insert:

			SELECT IFNULL(CAST(MAX(SALE_SEQ) AS UNSIGNED), 0)+1 
			  FROM DR_VOUCHER_SALE

    	INSERT INTO DR_VOUCHER_SALE (
			CO_DIV
			, SALE_DAY
			, SALE_SEQ
			, MS_NUM
			, VC_CD
			, DR_SERIAL_NO
			, LK_CO_DIV
			, LK_NUM
			, VC_FROM_DAY
			, VC_TO_DAY
			, VC_STATE
			, VC_LIMIT_CNT
			, VC_SERVICE_CNT
			, VC_USE_CNT
			, VC_REM_CNT
			, VC_REMARK
			, USE_YN
			, INPUT_STAFF
			, INPUT_DATETIME
			, INPUT_IP
    	) values (
    		#{coDiv}			
    		, #{saleDay}		
    		, #{saleSeq}			
    		, #{msNum}
    		, #{vcCd}	
    		, #{drSerialNo}	
    		, #{lkCoDiv}		
    		, #{lkNum}		
    		, #{vcFromDay}		
    		, #{vcToDay}		
    		, #{vcState}		
    		, #{vcLimitCnt}		
    		, #{vcServiceCnt}		
    		, #{vcUseCnt}		
    		, #{vcRemCnt}		
    		, #{vcRemark}	
    		, #{useYn}	
    		, #{inputStaff}			
    		, now()
			, #{ipAddr}						
    	)

mapper.update:
UPDATE DR_VOUCHER_SALE
   SET VC_REM_CNT 		= #{vcRemCnt} 
 WHERE CO_DIV 			= #{coDiv}
   AND SALE_DAY 		= #{saleDay}
   AND SALE_SEQ 		= #{saleSeq}

UPDATE DR_VOUCHER_SALE
   SET VC_REM_CNT 		= VC_REM_CNT - #{vcRemCnt} 
 WHERE CO_DIV 			= #{coDiv}
   AND SALE_DAY 		= #{saleDay}
   AND SALE_SEQ 		= #{saleSeq}
