mapper.insert:
    	INSERT INTO PRO_LICENSE (
			MS_NUM				
			, LIC_SEQ			
			, LIC_KIND			
			, LIC_REMARK
    	) values (
    		#{msNum}			
    		, #{licSeq}		
    		, #{licKind}			
    		, #{licRemark}	
    	)

mapper.update:
UPDATE PRO_LICENSE
   SET LIC_KIND 		= #{licKind}
     , LIC_REMARK 		= #{licRemark}
 WHERE MS_NUM 			= #{msNum}
   AND LIC_SEQ 			= #{licSeq}
