mapper.insert:
    	INSERT INTO PRO_NOTICE (
			MS_NUM				
			, NOTICE_DIV			
			, PRO_MUST_YN			
			, PRO_REMARK
			, NOTICE_OPEN_YN	
    	) values (
    		#{msNum}			
    		, #{noticeDiv}		
    		, #{proMustYn}			
    		, #{proRemark}
    		, #{noticeOpenYn}			
    	)

mapper.update:
UPDATE PRO_NOTICE
   SET PRO_MUST_YN 		= #{proMustYn}
     , PRO_REMARK 		= #{proRemark}
     , NOTICE_OPEN_YN 	= #{noticeOpenYn}
 WHERE MS_NUM 			= #{msNum}
   AND NOTICE_DIV 		= #{noticeDiv}

mapper.insert:
    	INSERT INTO PRO_LICENSE (
			MS_NUM				
			, LIC_SEQ			
			, LIC_KIND			
			, LIC_REMARK
    	) values (
    		#{msNum}			
    		, #{licSeq}		
    		, #{licKind}			
    		, #{licRemark}	
    	)

mapper.update:
UPDATE PRO_LICENSE
   SET LIC_KIND 		= #{licKind}
     , LIC_REMARK 		= #{licRemark}
 WHERE MS_NUM 			= #{msNum}
   AND LIC_SEQ 			= #{licSeq}
