mapper.insert:
    	INSERT INTO PRO_NOTICE (
			  MS_NUM				
			, NOTICE_DIV			
			, PRO_MUST_YN			
			, PRO_REMARK
			, NOTICE_OPEN_YN	
    	) values (
    		  #{msNum}			
    		, #{noticeDiv}		
    		, 'Y'		
    		, #{proRemark}
    		, 'Y'		
    	)

mapper.update:
UPDATE PRO_NOTICE
   SET MS_NUM = #{msNum}

   	 , PRO_MUST_YN 		= #{proMustYn}


     , PRO_REMARK 		= #{proRemark}


     , NOTICE_OPEN_YN 	= #{noticeOpenYn}

 WHERE MS_NUM 			= #{msNum}
   AND NOTICE_DIV 		= #{noticeDiv}

mapper.select:
	DELETE FROM PRO_LICENSE 
    WHERE 1 = 1
		AND MS_NUM = #{msNum}

mapper.insert:

            SELECT IFNULL(MAX(LIC_SEQ), 0) + 1
				FROM PRO_LICENSE
            WHERE 1 = 1
				AND MS_NUM = #{msNum}

    	INSERT INTO PRO_LICENSE (
			  MS_NUM				
			, LIC_SEQ			
			, LIC_KIND			
			, LIC_REMARK
    	) values (
    		  #{msNum}			
    		, #{licSeq}	
    		, #{licKind}			
    		, #{licRemark}	
    	)

mapper.update:
UPDATE PRO_LICENSE
   SET LIC_KIND 		= #{licKind}
     , LIC_REMARK 		= #{licRemark}
 WHERE MS_NUM 			= #{msNum}
   AND LIC_SEQ 			= #{licSeq}

mapper.insert:

            SELECT IFNULL(MAX(IMG_SEQ), 0) + 1
				FROM PRO_IMAGE
            WHERE 1 = 1
				AND MS_NUM = #{msNum}

    	INSERT INTO PRO_IMAGE (
			  MS_NUM				
			, IMG_DIV			
			, IMG_SEQ			
			, IMG_FILENAME
			, IMG_DATA
    	) values (
    		  #{msNum}			
    		, #{imgDiv}	
    		, #{imgSeq}	
    		, #{imgFilename}			
    		, #{imgData}	
    	)
